package ejercicio7;

public class Ejercicio7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int producto = 1;
		int contador = 0;
		for (int i = 1; contador < 10; i+=2) {
            producto *= i;
            contador ++;
           
           
		}
		System.out.println(producto);

	}

}
